mapper.update:
update user
set ut_id = #{utId,jdbcType=VARCHAR},
  u_name = #{uName,jdbcType=VARCHAR},
  u_password = #{uPassword,jdbcType=VARCHAR},
  u_delete = #{uDelete,jdbcType=BIT}
where u_id = #{uId,jdbcType=VARCHAR}

mapper.select:
select u_id, ut_id, u_name, u_password, u_delete
from user
where u_id = #{uId,jdbcType=VARCHAR}
